            21 => 
            array (
                'id' => 22,
                'title' => 'La Prueba de Abraham',
                'description' => 'Descubre la profunda fe de Abraham al ser puesto a prueba por Dios con su hijo Isaac.',
                'featured_image' => NULL,
                'keywords' => 'Abraham, prueba, fe, Dios, Isaac',
                'libro_id' => 1,
