            411 => 
            array (
                'id' => 1412,
                'capitulo_id' => 46,
                'pericopa_id' => 185,
                'num_versiculo' => 26,
                'contenido' => 'Todas las personas que fueron con Jacob a Egipto, procedentes de sus lomos, sin contar las esposas de los hijos de Jacob, todas las personas fueron sesenta y seis.',
                'referencia' => 'Génesis 46:26',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:06',
                'updated_at' => '2024-03-12 15:25:06',
