            174 => 
            array (
                'id' => 2175,
                'capitulo_id' => 73,
                'pericopa_id' => 255,
                'num_versiculo' => 10,
                'contenido' => 'Seis años sembrarás tu tierra y recogerás su cosecha;',
                'referencia' => 'Exodo 23:10',
