            array (
                'id' => 2178,
                'capitulo_id' => 73,
                'pericopa_id' => 255,
                'num_versiculo' => 13,
                'contenido' => 'Y guardad todo lo que os he dicho. Y nombre de otros dioses no mencionaréis, ni se oirá de vuestra boca.',
                'referencia' => 'Exodo 23:13',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:06',
