            array (
                'id' => 2373,
                'capitulo_id' => 79,
                'pericopa_id' => 270,
                'num_versiculo' => 16,
                'contenido' => 'Y matarás el carnero, y tomarás su sangre y la rociarás sobre el altar alrededor.',
