            array (
                'id' => 2376,
                'capitulo_id' => 79,
                'pericopa_id' => 270,
                'num_versiculo' => 19,
                'contenido' => 'Tomarás luego el otro carnero, y Aarón y sus hijos pondrán sus manos sobre la cabeza del carnero.',
