            445 => 
            array (
                'id' => 2446,
                'capitulo_id' => 81,
                'pericopa_id' => 276,
                'num_versiculo' => 5,
                'contenido' => 'y en el labrado de piedras para engastarlas y en el tallado de madera, y para trabajar en toda clase de artesanía.',
                'referencia' => 'Exodo 31:5',
