            ),
            447 => 
            array (
                'id' => 2448,
                'capitulo_id' => 81,
                'pericopa_id' => 276,
                'num_versiculo' => 7,
                'contenido' => 'el tabernáculo de reunión, y el arca del testimonio, y el propiciatorio que está sobre ella, y todos los enseres del tabernáculo,',
                'referencia' => 'Exodo 31:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
