            448 => 
            array (
                'id' => 2449,
                'capitulo_id' => 81,
                'pericopa_id' => 276,
                'num_versiculo' => 8,
                'contenido' => 'y la mesa con sus utensilios, y el candelabro puro con todos sus utensilios, y el altar del incienso,',
                'referencia' => 'Exodo 31:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
