            449 => 
            array (
                'id' => 2450,
                'capitulo_id' => 81,
                'pericopa_id' => 276,
                'num_versiculo' => 9,
                'contenido' => 'y el altar del holocausto con todos sus utensilios, y la fuente con su base,',
                'referencia' => 'Exodo 31:9',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:07',
