            450 => 
            array (
                'id' => 2451,
                'capitulo_id' => 81,
                'pericopa_id' => 276,
                'num_versiculo' => 10,
                'contenido' => 'y las vestiduras del servicio, y las santas vestiduras para Aarón el sacerdote, y las vestiduras de sus hijos, para que ejerzan el sacerdocio,',
                'referencia' => 'Exodo 31:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:07',
