            ),
            451 => 
            array (
                'id' => 2452,
                'capitulo_id' => 81,
                'pericopa_id' => 276,
                'num_versiculo' => 11,
                'contenido' => 'y el aceite de la unción y el incienso aromático para el lugar santo; harán conforme a todo lo que te he mandado.',
                'referencia' => 'Exodo 31:11',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:07',
