            417 => 
            array (
                'id' => 3418,
                'capitulo_id' => 112,
                'pericopa_id' => 326,
                'num_versiculo' => 28,
                'contenido' => 'Y sea vaca u oveja, no degollaréis en el mismo día a ella y a su cría.',
                'referencia' => 'Levítico 22:28',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:08',
                'updated_at' => '2024-03-12 15:25:08',
