            array (
                'id' => 3847,
                'capitulo_id' => 123,
                'pericopa_id' => 344,
                'num_versiculo' => 3,
                'contenido' => 'se abstendrá de vino y de sidra; no beberá vinagre de vino ni vinagre de sidra, ni beberá ningún jugo de uva, ni tampoco comerá uvas frescas ni secas.',
