            349 => 
            array (
                'id' => 4350,
                'capitulo_id' => 137,
                'pericopa_id' => 367,
                'num_versiculo' => 18,
                'contenido' => 'Y Edom le respondió: No pasarás por mi país; de otra manera, saldré contra ti armado.',
                'referencia' => 'Números 20:18',
                'imagen' => '',
