            354 => 
            array (
                'id' => 4355,
                'capitulo_id' => 137,
                'pericopa_id' => 368,
                'num_versiculo' => 23,
                'contenido' => 'Y Jehová habló a Moisés y a Aarón en el monte Hor, en los confines de la tierra de Edom, diciendo:',
                'referencia' => 'Números 20:23',
                'imagen' => '',
