                'capitulo_id' => 138,
                'pericopa_id' => 371,
                'num_versiculo' => 10,
                'contenido' => 'Y partieron los hijos de Israel y acamparon en Obot.',
                'referencia' => 'Números 21:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:08',
                'updated_at' => '2024-03-12 15:25:08',
