            array (
                'id' => 4373,
                'capitulo_id' => 138,
                'pericopa_id' => 371,
                'num_versiculo' => 11,
                'contenido' => 'Y partieron de Obot y acamparon en Ije-abarim, en el desierto que está delante de Moab, hacia el nacimiento del sol.',
                'referencia' => 'Números 21:11',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:08',
                'updated_at' => '2024-03-12 15:25:08',
            ),
            372 => 
            array (
                'id' => 4374,
                'capitulo_id' => 138,
                'pericopa_id' => 371,
                'num_versiculo' => 12,
                'contenido' => 'Partieron de allí y acamparon en el valle de Zered.',
