            ),
            374 => 
            array (
                'id' => 4376,
                'capitulo_id' => 138,
                'pericopa_id' => 371,
                'num_versiculo' => 14,
                'contenido' => 'Por tanto se dice en el libro de las batallas de Jehová: Lo que hizo en el Mar Rojo y en los arroyos del Arnón;',
                'referencia' => 'Números 21:14',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:08',
                'updated_at' => '2024-03-12 15:25:08',
            ),
            375 => 
            array (
                'id' => 4377,
                'capitulo_id' => 138,
                'pericopa_id' => 371,
                'num_versiculo' => 15,
                'contenido' => 'y a la corriente de los arroyos que va a parar en Ar y descansa en la frontera de Moab.',
                'referencia' => 'Números 21:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:08',
                'updated_at' => '2024-03-12 15:25:08',
