                'id' => 4381,
                'capitulo_id' => 138,
                'pericopa_id' => 371,
                'num_versiculo' => 19,
                'contenido' => 'y de Matana a Nahaliel, y de Nahaliel a Bamot,',
                'referencia' => 'Números 21:19',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:08',
                'updated_at' => '2024-03-12 15:25:08',
