            380 => 
            array (
                'id' => 4382,
                'capitulo_id' => 138,
                'pericopa_id' => 371,
                'num_versiculo' => 20,
                'contenido' => 'y de Bamot al valle que está en los campos de Moab, y a la cumbre del Pisga, que mira hacia Jesimón.',
                'referencia' => 'Números 21:20',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:08',
                'updated_at' => '2024-03-12 15:25:08',
