            381 => 
            array (
                'id' => 4383,
                'capitulo_id' => 138,
                'pericopa_id' => 372,
                'num_versiculo' => 21,
                'contenido' => 'Y envió Israel embajadores a Sehón, rey de los amorreos, diciendo:',
