            410 => 
            array (
                'id' => 4412,
                'capitulo_id' => 139,
                'pericopa_id' => 374,
                'num_versiculo' => 15,
                'contenido' => 'Y volvió Balac a enviar otra vez príncipes, más numerosos y más honorables que los otros,',
                'referencia' => 'Números 22:15',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:09',
                'updated_at' => '2024-03-12 15:25:09',
            ),
            411 => 
            array (
                'id' => 4413,
                'capitulo_id' => 139,
                'pericopa_id' => 374,
                'num_versiculo' => 16,
                'contenido' => 'los cuales fueron a Balaam y le dijeron: Así dice Balac hijo de Zipor: Te ruego que no dejes de venir a mí,',
                'referencia' => 'Números 22:16',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:09',
                'updated_at' => '2024-03-12 15:25:09',
