            array (
                'id' => 6015,
                'capitulo_id' => 194,
                'pericopa_id' => 494,
                'num_versiculo' => 17,
                'contenido' => 'y haciendo acercar la tribu de Judá, fue tomada la familia de los de Zera; haciendo luego acercar la familia de los de Zera hombre por hombre, fue tomado Zabdi;',
                'referencia' => 'Josué 7:17',
                'imagen' => '',
