            15 => 
            array (
                'id' => 6016,
                'capitulo_id' => 194,
                'pericopa_id' => 494,
                'num_versiculo' => 18,
                'contenido' => 'e hizo acercar su casa hombre por hombre, y fue tomado Acán, hijo de Carmi, hijo de Zabdi, hijo de Zera, de la tribu de Judá.',
                'referencia' => 'Josué 7:18',
