            110 => 
            array (
                'id' => 6612,
                'capitulo_id' => 214,
                'pericopa_id' => 535,
                'num_versiculo' => 21,
                'contenido' => 'Y Aod alargó su mano izquierda, y tomó el puñal de su lado derecho y se lo clavó por el vientre',
                'referencia' => 'Jueces 3:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:11',
                'updated_at' => '2024-03-12 15:25:11',
