            287 => 
            array (
                'id' => 7292,
                'capitulo_id' => 238,
                'pericopa_id' => 573,
                'num_versiculo' => 26,
                'contenido' => 'Y el niño Samuel iba creciendo en estatura y en gracia delante de Dios y delante de los hombres.',
                'referencia' => '1 Samuel 2:26',
