            288 => 
            array (
                'id' => 7293,
                'capitulo_id' => 238,
                'pericopa_id' => 573,
                'num_versiculo' => 27,
                'contenido' => 'Y vino un varón de Dios a Elí y le dijo: Así ha dicho Jehová: ¿No me manifesté yo claramente a la casa de tu padre cuando estaban en Egipto en la casa de Faraón?',
                'referencia' => '1 Samuel 2:27',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:11',
                'updated_at' => '2024-03-12 15:25:11',
