            55 => 
            array (
                'id' => 7560,
                'capitulo_id' => 250,
                'pericopa_id' => 586,
                'num_versiculo' => 26,
                'contenido' => 'Entró, pues, el pueblo en el bosque, y he aquí que la miel corría; mas no hubo quien se llevase la mano a la boca, porque el pueblo temía el juramento.',
                'referencia' => '1 Samuel 14:26',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:12',
                'updated_at' => '2024-03-12 15:25:12',
