            59 => 
            array (
                'id' => 7564,
                'capitulo_id' => 250,
                'pericopa_id' => 586,
                'num_versiculo' => 30,
                'contenido' => '¿Cuánto más si el pueblo hubiera hoy comido del botín que encontró de sus enemigos? ¿No se habría hecho ahora mayor estrago entre los filisteos?',
                'referencia' => '1 Samuel 14:30',
