            62 => 
            array (
                'id' => 7567,
                'capitulo_id' => 250,
                'pericopa_id' => 586,
                'num_versiculo' => 33,
                'contenido' => 'Y le avisaron a Saúl, diciéndole: El pueblo peca contra Jehová, comiendo la carne con la sangre. Y él dijo: Vosotros habéis transgredido; rodadme ahora acá una piedra grande.',
                'referencia' => '1 Samuel 14:33',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:12',
                'updated_at' => '2024-03-12 15:25:12',
