            61 => 
            array (
                'id' => 8066,
                'capitulo_id' => 268,
                'pericopa_id' => 609,
                'num_versiculo' => 18,
                'contenido' => 'y dijo también que enseñasen a los hijos de Judá a usar el arco. He aquí que está escrito en el libro de Jaser:',
                'referencia' => '2 Samuel 1:18',
