            77 => 
            array (
                'id' => 8082,
                'capitulo_id' => 269,
                'pericopa_id' => 610,
                'num_versiculo' => 7,
                'contenido' => 'Fortalézcanse, pues, ahora vuestras manos y sed valientes; pues muerto Saúl, vuestro señor, los de la casa de Judá me han ungido rey sobre ellos.',
                'referencia' => '2 Samuel 2:7',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:12',
                'updated_at' => '2024-03-12 15:25:12',
            ),
            78 => 
            array (
                'id' => 8083,
                'capitulo_id' => 269,
                'pericopa_id' => 611,
                'num_versiculo' => 8,
                'contenido' => 'Pero Abner hijo de Ner, general del ejército de Saúl, tomó a Is-boset hijo de Saúl y lo llevó a Mahanaim,',
                'referencia' => '2 Samuel 2:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:12',
                'updated_at' => '2024-03-12 15:25:12',
            ),
            79 => 
            array (
                'id' => 8084,
                'capitulo_id' => 269,
                'pericopa_id' => 611,
                'num_versiculo' => 9,
                'contenido' => 'y lo hizo rey sobre Galaad, y sobre Gesuri, y sobre Jezreel, y sobre Efraín, y sobre Benjamín y sobre todo Israel.',
