            array (
                'id' => 8141,
                'capitulo_id' => 270,
                'pericopa_id' => 615,
                'num_versiculo' => 33,
                'contenido' => 'Y endechando el rey al mismo Abner, decía: ¿Había de morir Abner como muere un insensato?',
                'referencia' => '2 Samuel 3:33',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:12',
                'updated_at' => '2024-03-12 15:25:12',
