            array (
                'id' => 8247,
                'capitulo_id' => 275,
                'pericopa_id' => 625,
                'num_versiculo' => 11,
                'contenido' => 'los cuales el rey David dedicó a Jehová, junto con la plata y el oro que había dedicado de todas las naciones que había sometido:',
