            array (
                'id' => 8319,
                'capitulo_id' => 279,
                'pericopa_id' => 630,
                'num_versiculo' => 6,
                'contenido' => 'Y él debe pagar cuatro veces por la corderita, porque hizo tal cosa y no tuvo misericordia.',
