            array (
                'id' => 8325,
                'capitulo_id' => 279,
                'pericopa_id' => 630,
                'num_versiculo' => 12,
                'contenido' => 'Porque tú lo hiciste en secreto, pero yo haré esto delante de todo Israel y a pleno sol.',
