            365 => 
            array (
                'id' => 8371,
                'capitulo_id' => 280,
                'pericopa_id' => 633,
                'num_versiculo' => 27,
                'contenido' => 'Y como Absalón le insistió, dejó ir con él a Amnón y a todos los hijos del rey.',
                'referencia' => '2 Samuel 13:27',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:12',
                'updated_at' => '2024-03-12 15:25:12',
