            53 => 
            array (
                'id' => 8559,
                'capitulo_id' => 286,
                'pericopa_id' => 639,
                'num_versiculo' => 21,
                'contenido' => 'Y Abisai hijo de Sarvia respondió y dijo: ¿No ha de morir por esto Simei, que maldijo al ungido de Jehová?',
                'referencia' => '2 Samuel 19:21',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
