            366 => 
            array (
                'id' => 8872,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 1,
                'contenido' => 'Y el rey Salomón fue rey sobre todo Israel.',
                'referencia' => '1 Reyes 4:1',
