            367 => 
            array (
                'id' => 8873,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 2,
                'contenido' => 'Y éstos fueron los oficiales que tuvo: Azarías hijo de Sadoc, el sacerdote;',
                'referencia' => '1 Reyes 4:2',
                'imagen' => '',
                'pie_imagen' => '',
