            368 => 
            array (
                'id' => 8874,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 3,
                'contenido' => 'Elihoref y Ahías, hijos de Sisa, escribas; Josafat hijo de Ahilud, cronista;',
                'referencia' => '1 Reyes 4:3',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
