            369 => 
            array (
                'id' => 8875,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 4,
                'contenido' => 'Benaía hijo de Joiada estaba sobre el ejército; y Sadoc y Abiatar eran los sacerdotes;',
                'referencia' => '1 Reyes 4:4',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
