            370 => 
            array (
                'id' => 8876,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 5,
                'contenido' => 'Azarías hijo de Natán estaba sobre los gobernadores; y Zabud hijo de Natán era ministro principal y amigo del rey;',
                'referencia' => '1 Reyes 4:5',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
