            371 => 
            array (
                'id' => 8877,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 6,
                'contenido' => 'y Ahisar era mayordomo; y Adoniram hijo de Abda estaba sobre el tributo laboral.',
                'referencia' => '1 Reyes 4:6',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
