            373 => 
            array (
                'id' => 8879,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 8,
                'contenido' => 'Y éstos son los nombres de ellos: el hijo de Hur, en los montes de Efraín;',
                'referencia' => '1 Reyes 4:8',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
