            array (
                'id' => 8880,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 9,
                'contenido' => 'el hijo de Decar, en Macaz, y en Saalbim, y en Bet-semes y en Elónbet-hanán;',
                'referencia' => '1 Reyes 4:9',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
