                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 10,
                'contenido' => 'el hijo de Hesed, en Arubot; éste tenía también Soco y toda la tierra de Hefer.',
                'referencia' => '1 Reyes 4:10',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
