            376 => 
            array (
                'id' => 8882,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 11,
                'contenido' => 'El hijo de Abinadab, en todos los territorios de Dor; éste tenía por esposa a Tafat, hija de Salomón;',
                'referencia' => '1 Reyes 4:11',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
