            377 => 
            array (
                'id' => 8883,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 12,
                'contenido' => 'Baana hijo de Ahilud, en Taanac y Meguido, y en toda Bet-seán, que está cerca de Saretán, más abajo de Jezreel, desde Bet-seán hasta Abel-mehola, y hasta el otro lado de Jocmeam;',
                'referencia' => '1 Reyes 4:12',
                'imagen' => '',
                'pie_imagen' => '',
                'video' => '',
                'created_at' => '2024-03-12 15:25:13',
                'updated_at' => '2024-03-12 15:25:13',
            ),
            378 => 
            array (
                'id' => 8884,
                'capitulo_id' => 295,
                'pericopa_id' => 658,
                'num_versiculo' => 13,
                'contenido' => 'el hijo de Geber, en Ramot de Galaad; éste tenía también las ciudades de Jair hijo de Manasés, las cuales estaban en Galaad; tenía también la provincia de Argob, que estaba en Basán, sesenta grandes ciudades con muro y cerrojos de bronce;',
                'referencia' => '1 Reyes 4:13',
                'imagen' => '',
